public class ZooKeeperSaslServerTest {

    @Test
    public void testCreateSaslServerUsesNewCallbackHandler() throws Exception {
        // Create a mock Login that tracks whether newCallbackHandler() was called
        Login mockLogin = new Login() {
            boolean newHandlerCalled = false;

            @Override
            public CallbackHandler newCallbackHandler() {
                newHandlerCalled = true;
                return super.newCallbackHandler();
            }

            @Override
            public Subject getSubject() {
                return new Subject();
            }
        };

        ZooKeeperSaslServer server = new ZooKeeperSaslServer();
        SaslServer saslServer = server.createSaslServer(mockLogin);

        // This assertion will:
        // - FAIL on buggy code (using login.callbackHandler directly)
        // - PASS on fixed code (using login.newCallbackHandler())
        assertTrue("Should use newCallbackHandler() instead of direct callbackHandler access", 
            ((TestLogin)mockLogin).newHandlerCalled);
    }

    // Helper class to expose protected methods for testing
    private static class TestLogin extends Login {
        public boolean newHandlerCalled = false;

        @Override
        public CallbackHandler newCallbackHandler() {
            newHandlerCalled = true;
            return super.newCallbackHandler();
        }

        @Override
        public Subject getSubject() {
            return new Subject();
        }
    }
}

