import org.junit.Test;
import java.util.concurrent.atomic.AtomicBoolean;
import java.util.function.Consumer;
import static org.junit.Assert.*;

public class ServiceUtilsTest {

    @Test
    public void testSystemExitProcedureThreadSafety() throws InterruptedException {
        // Setup
        AtomicBoolean testFailed = new AtomicBoolean(false);
        Consumer<Integer> original = ServiceUtils.getSystemExitProcedure();

        // Create threads that will modify and read the field concurrently
        Thread modifier = new Thread(() -> {
            for (int i = 0; i < 1000; i++) {
                ServiceUtils.setSystemExitProcedure(code -> {});
            }
        });

        Thread reader = new Thread(() -> {
            for (int i = 0; i < 1000; i++) {
                try {
                    Consumer<Integer> current = ServiceUtils.getSystemExitProcedure();
                    if (current == null) {
                        testFailed.set(true);
                    }
                } catch (NullPointerException e) {
                    testFailed.set(true);
                }
            }
        });

        // Run threads
        modifier.start();
        reader.start();

        // Wait for completion
        modifier.join();
        reader.join();

        // Restore original
        ServiceUtils.setSystemExitProcedure(original);

        // Verify no failures occurred
        assertFalse("NullPointerException occurred or null value was read", testFailed.get());
    }

    // Helper methods to access package-private members (assuming these exist)
    private static class ServiceUtils {
        public static Consumer<Integer> getSystemExitProcedure() {
            return ServiceUtils.systemExitProcedure;
        }

        public static void setSystemExitProcedure(Consumer<Integer> procedure) {
            ServiceUtils.systemExitProcedure = procedure;
        }
    }
}

