import org.apache.zookeeper.KeeperException;
import org.junit.Test;
import static org.junit.Assert.*;

public class ZKCliErrorMessageTest {

    @Test
    public void testNoAuthExceptionMessage() {
        // Create a NoAuthException with a test path
        KeeperException.NoAuthException exception = new KeeperException.NoAuthException("/test/path");

        // Call the method under test (would be in the same class)
        String actualMessage = getMessage(exception);

        // Test against expected values
        // This will FAIL on buggy code ("Authentication is not valid")
        // and PASS on fixed code ("Insufficient permission")
        assertEquals("Insufficient permission : /test/path", actualMessage);
    }

    // This would be the method being tested (copied from the patch)
    private static String getMessage(Throwable cause) {
        if (!(cause instanceof KeeperException)) {
            return cause.getMessage();
        }

        KeeperException keeperException = (KeeperException) cause;
        if (keeperException instanceof KeeperException.InvalidACLException) {
            return "Acl is not valid : " + keeperException.getPath();
        } else if (keeperException instanceof KeeperException.NoAuthException) {
            return "Insufficient permission : " + keeperException.getPath();
        } else if (keeperException instanceof KeeperException.BadArgumentsException) {
            return "Arguments are not valid : " + keeperException.getPath();
        } else if (keeperException instanceof KeeperException.BadVersionException) {
            return "Version is not valid : " + keeperException.getPath();
        } else {
            return keeperException.getMessage();
        }
    }
}

