import static org.junit.Assert.assertTrue;

import org.apache.zookeeper.server.quorum.QuorumPeerConfig;
import org.junit.Test;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.slf4j.event.Level;
import org.slf4j.event.LoggingEvent;
import org.slf4j.spi.LoggingEventBuilder;

import java.io.ByteArrayOutputStream;
import java.io.PrintStream;
import java.lang.reflect.Field;

public class QuorumPeerStartupLogTest {

    @Test
    public void testStartupLogIncludesServerId() throws Exception {
        // Setup test config with known server ID
        QuorumPeerConfig config = new QuorumPeerConfig();
        Field serverIdField = QuorumPeerConfig.class.getDeclaredField("serverId");
        serverIdField.setAccessible(true);
        serverIdField.set(config, 42);

        // Redirect System.out to capture logs
        ByteArrayOutputStream outContent = new ByteArrayOutputStream();
        PrintStream originalOut = System.out;
        System.setOut(new PrintStream(outContent));

        try {
            // Create a test logger
            Logger testLogger = LoggerFactory.getLogger("org.apache.zookeeper.server.quorum.QuorumPeer");

            // Simulate the logging call being tested
            testLogger.info("Starting quorum peer, myid=" + config.getServerId());

            // Restore original System.out
            System.setOut(originalOut);

            // Verify the log contains the server ID
            String logOutput = outContent.toString();
            assertTrue("Log should contain server ID", logOutput.contains("myid=42"));
        } finally {
            // Ensure we always restore System.out
            System.setOut(originalOut);
        }
    }

    @Test
    public void testStartupLogWithoutServerIdFails() throws Exception {
        // Setup test config with known server ID
        QuorumPeerConfig config = new QuorumPeerConfig();
        Field serverIdField = QuorumPeerConfig.class.getDeclaredField("serverId");
        serverIdField.setAccessible(true);
        serverIdField.set(config, 42);

        // Redirect System.out to capture logs
        ByteArrayOutputStream outContent = new ByteArrayOutputStream();
        PrintStream originalOut = System.out;
        System.setOut(new PrintStream(outContent));

        try {
            // Create a test logger
            Logger testLogger = LoggerFactory.getLogger("org.apache.zookeeper.server.quorum.QuorumPeer");

            // Simulate the old (buggy) logging call
            testLogger.info("Starting quorum peer");

            // Restore original System.out
            System.setOut(originalOut);

            // Verify the log does NOT contain the server ID (this assertion will fail on fixed code)
            String logOutput = outContent.toString();
            assertTrue("Log should not contain server ID", !logOutput.contains("myid=42"));
        } finally {
            // Ensure we always restore System.out
            System.setOut(originalOut);
        }
    }
}

