import org.apache.log4j.Logger;
import org.junit.Test;
import org.mockito.ArgumentCaptor;

public class ClientCnxnEventThreadTest {

    @Test
    public void testErrorLoggingFormat() {
        // Setup
        Logger mockLogger = mock(Logger.class);
        ClientCnxn.EventThread eventThread = new ClientCnxn.EventThread();

        // Inject mock logger (would need reflection in real test)
        // This is simplified - actual test would need to handle field injection
        eventThread.LOG = mockLogger;

        // Simulate error condition
        Throwable testException = new NullPointerException();
        Object testEvent = new Object(); // Would be actual event in real code

        try {
            // Trigger processing with null watcher to cause NPE
            eventThread.processEvent(testEvent);
        } catch (NullPointerException e) {
            // Expected - verify logging format

            // Capture the logged message
            ArgumentCaptor<String> messageCaptor = ArgumentCaptor.forClass(String.class);
            ArgumentCaptor<Throwable> throwableCaptor = ArgumentCaptor.forClass(Throwable.class);

            verify(mockLogger).error(messageCaptor.capture(), throwableCaptor.capture());

            // Assert the message format matches the fixed version
            String loggedMessage = messageCaptor.getValue();
            if (loggedMessage.endsWith(" ")) {
                throw new AssertionError("Log message should not end with space");
            }

            // Verify it ends with period (fixed version)
            if (!loggedMessage.endsWith(".")) {
                throw new AssertionError("Log message should end with period");
            }
        }
    }
}

