import org.junit.Test;
import static org.junit.Assert.*;

import java.io.File;
import java.io.FileWriter;
import java.io.IOException;
import java.io.StringWriter;

public class VersionInfoTest {

    static class Version {
        public final int maj;
        public final int min;
        public final int micro;
        public final String qualifier;

        public Version(int maj, int min, int micro, String qualifier) {
            this.maj = maj;
            this.min = min;
            this.micro = micro;
            this.qualifier = qualifier;
        }
    }

    @Test
    public void testGenerateFileWithNullQualifier() throws IOException {
        Version version = new Version(3, 6, 0, null);
        StringWriter writer = new StringWriter();

        // This would be the method under test that contains the patched line
        generateVersionInfo(writer, version, "-1");

        String output = writer.toString();
        assertTrue("QUALIFIER should be empty string when null", 
            output.contains("String QUALIFIER=\"\";"));
    }

    @Test
    public void testGenerateFileWithNonNullQualifier() throws IOException {
        Version version = new Version(3, 6, 0, "SNAPSHOT");
        StringWriter writer = new StringWriter();

        generateVersionInfo(writer, version, "-1");

        String output = writer.toString();
        assertTrue("QUALIFIER should contain qualifier value",
            output.contains("String QUALIFIER=\"SNAPSHOT\";"));
    }

    // This simulates the method that was patched
    private void generateVersionInfo(StringWriter w, Version version, String rev) throws IOException {
        w.write("    int MAJOR=" + version.maj + ";\n");
        w.write("    int MINOR=" + version.min + ";\n");
        w.write("    int MICRO=" + version.micro + ";\n");
        // This is the patched line - change between null and "\"\"" for test
        w.write("    String QUALIFIER=" + (version.qualifier == null ? "\"\"" : "\"" + version.qualifier + "\"") + ";\n");

        if (rev.equals("-1")) {
            System.out.println("Unknown REVISION number, using " + rev);
        }
    }
}

