import org.junit.Test;
import java.io.StringReader;
import java.util.regex.Pattern;
import static org.junit.Assert.assertTrue;

public class QuorumPeerAddressTest {

    @Test
    public void testAddressUnreachableErrorMessage() {
        // Simulated log output that would be produced in both cases
        String logOutput = "2023-01-01 12:00:00 [WARN] Cannot open channel to 127.0.0.1 at election address 127.0.0.1:3888\n" +
                          "Some other log message\n";

        // The pattern that was changed in the patch
        Pattern fixedPattern = Pattern.compile(".*Cannot open channel to .* at election address .*");

        // The old pattern that was replaced
        Pattern buggyPattern = Pattern.compile(".*None of the addresses .* are reachable for sid 2");

        // Test with new pattern - should pass
        boolean foundWithFixedPattern = false;
        try (LineNumberReader reader = new LineNumberReader(new StringReader(logOutput))) {
            String line;
            while ((line = reader.readLine()) != null) {
                if (fixedPattern.matcher(line).matches()) {
                    foundWithFixedPattern = true;
                    break;
                }
            }
        } catch (Exception e) {
            // Should not happen in this test
        }
        assertTrue("Should match the fixed pattern", foundWithFixedPattern);

        // Test with old pattern - should fail (demonstrating the bug)
        boolean foundWithBuggyPattern = false;
        try (LineNumberReader reader = new LineNumberReader(new StringReader(logOutput))) {
            String line;
            while ((line = reader.readLine()) != null) {
                if (buggyPattern.matcher(line).matches()) {
                    foundWithBuggyPattern = true;
                    break;
                }
            }
        } catch (Exception e) {
            // Should not happen in this test
        }
        assertTrue("This assertion fails with buggy code", foundWithBuggyPattern);
    }
}

