public class ServerStartupTest {
    private static final Logger LOG = LoggerFactory.getLogger(ServerStartupTest.class);
    private static final int CONNECTION_TIMEOUT = 30000;

    @Test
    public void testServerStartupWithMultipleAddresses() throws Exception {
        // Simulate multiple server addresses (comma-separated)
