@RunWith(MockitoJUnitRunner.class)
public class SessionTerminationTest {

    @Mock
    private ZooKeeper zk;
    @Mock
    private Logger logger;

    private static class TestSession {
        private final Logger LOG;
        private final long sessionId;
        private final ZooKeeper zk;

        public TestSession(Logger logger, long sessionId, ZooKeeper zk) {
            this.LOG = logger;
            this.sessionId = sessionId;
            this.zk = zk;
        }

        public void terminateSession() {
            try {
                zk.closeSession(sessionId);
            } catch (Exception e) {
                LOG.warn("Unable to closeSession() for session: 0x{}", sessionId, e);
            }
        }

        // Buggy version for testing failure
        public void terminateSessionBuggy() {
            try {
                zk.closeSession(sessionId);
            } catch (Exception e) {
                LOG.warn("Unable to closeSession() for session: 0x" + sessionId, e);
            }
        }
    }

    @Test
    public void testLogFormatFixed() throws Exception {
        long testSessionId = 12345L;
        Exception testException = new Exception("Test exception");

        when(zk.closeSession(testSessionId)).thenThrow(testException);

        TestSession session = new TestSession(logger, testSessionId, zk);
        session.terminateSession();

        verify(logger).warn("Unable to closeSession() for session: 0x{}", testSessionId, testException);
    }

    @Test(expected = AssertionError.class)
    public void testLogFormatBuggy() throws Exception {
        long testSessionId = 12345L;
        Exception testException = new Exception("Test exception");

        when(zk.closeSession(testSessionId)).thenThrow(testException);

        TestSession session = new TestSession(logger, testSessionId, zk);
        session.terminateSessionBuggy();

        // This will fail because the buggy version uses string concatenation
        verify(logger).warn("Unable to closeSession() for session: 0x{}", testSessionId, testException);
    }
}

