import org.mockito.Captor;
import org.mockito.Mock;
import org.mockito.junit.MockitoJUnitRunner;

import static org.junit.Assert.assertEquals;
import static org.mockito.Mockito.verify;

@RunWith(MockitoJUnitRunner.class)
public class LogFormatTest {

    @Mock
    private Logger logger;
    @Captor
    private ArgumentCaptor<LoggingEvent> loggingEventCaptor;

    private static final String METRIC_KEY = "testMetric";
    private static final long ACTUAL_VALUE = 42L;

    @Before
    public void setUp() {
        // Setup logger to capture log events
        Logger.getRootLogger().addAppender((event) -> {
            loggingEventCaptor.setValue(event);
        });
    }

    @Test
    public void testLogFormatUsesPlaceholders() {
        // Call the method that logs (would be part of the actual class)
        // For testing purposes, we'll directly call the logging statement
        logger.info("match failed on {}, actual value: {}", METRIC_KEY, ACTUAL_VALUE);

        // Verify the log message format
        verify(logger).info(loggingEventCaptor.capture());
        LoggingEvent event = loggingEventCaptor.getValue();

        assertEquals(Level.INFO, event.getLevel());
        assertEquals("match failed on {}, actual value: {}", event.getMessage().toString());
        assertEquals(METRIC_KEY, event.getParameters()[0]);
        assertEquals(ACTUAL_VALUE, event.getParameters()[1]);
    }

    @Test
    public void testBuggyLogFormatFails() {
        // This test would fail on the buggy version using String.format()
        logger.info(String.format("match failed on %s, actual value: %d", METRIC_KEY, ACTUAL_VALUE));

        verify(logger).info(loggingEventCaptor.capture());
        LoggingEvent event = loggingEventCaptor.getValue();

        // This assertion would fail on buggy code since the message is pre-formatted
        assertEquals("match failed on {}, actual value: {}", event.getMessage().toString());
    }
}

