@RunWith(MockitoJUnitRunner.class)
public class LoggingBehaviorTest {

    @Mock
    private Logger logger;

    @Captor
    private ArgumentCaptor<LoggingEvent> loggingEventCaptor;

    @Test
    public void testExceptionLoggingBehavior() {
        // Simulate the scenario that would trigger the logging
        Exception testException = new Exception("Test exception");

        // Call the method that contains the logging (simulated here)
        try {
            throw testException;
        } catch (Exception e) {
            // This is the fixed version - should use debug level and pass exception properly
            logger.debug("Got exception as expected", e);

            // Verify the logging behavior
            verify(logger).log(loggingEventCaptor.capture());
            LoggingEvent event = loggingEventCaptor.getValue();

            // Assert the log level is DEBUG (not INFO)
            assertEquals(Level.DEBUG, event.getLevel());

            // Assert the message is correct
            assertEquals("Got exception as expected", event.getMessage());

            // Assert the throwable is properly passed
            assertTrue(event.getThrowableInformation() != null);
            assertEquals(testException, event.getThrowableInformation().getThrowable());
        }
    }

    @Test
    public void testBuggyLoggingBehaviorShouldFail() {
        Exception testException = new Exception("Test exception");

        try {
            throw testException;
        } catch (Exception e) {
            // This is the buggy version - uses INFO level and string concatenation
            logger.info("Got exception as expected: " + e);

            verify(logger).log(loggingEventCaptor.capture());
            LoggingEvent event = loggingEventCaptor.getValue();

            // These assertions will fail for the buggy code
            assertEquals(Level.DEBUG, event.getLevel());  // Should be INFO in buggy version
            assertTrue(!event.getMessage().toString().contains("Got exception as expected: "));  // Should contain concatenation
            assertTrue(event.getThrowableInformation() != null);  // Throwable not properly passed in buggy version
        }
    }
}

