    public void setUp() {
        learnerHandler = new TestableLearnerHandler();
        metrics = ServerMetrics.getMetrics();
        metrics.resetAll();
    }

    @Test
    public void testZeroLatencyIsCounted() throws Exception {
        // Create a packet with current time to simulate 0 latency
        QuorumPacket qp = new QuorumPacket();
        TestableLearnerHandler.TestRequestHeader hdr = 
            learnerHandler.new TestRequestHeader(System.currentTimeMillis());

        // Process the packet
        learnerHandler.processPacketWithHeader(qp, hdr);

        // Verify that the 0 latency was counted
        assertEquals(1, metrics.getProposalLatency().getCount());
        assertEquals(0, metrics.getProposalLatency().getMin());
    }

    @Test
    public void testPositiveLatencyIsCounted() throws Exception {
        // Create a packet with time in past to simulate positive latency
        QuorumPacket qp = new QuorumPacket();
        TestableLearnerHandler.TestRequestHeader hdr = 
            learnerHandler.new TestRequestHeader(System.currentTimeMillis() - 10);

        // Process the packet
        learnerHandler.processPacketWithHeader(qp, hdr);

        // Verify that the positive latency was counted
        assertEquals(1, metrics.getProposalLatency().getCount());
        assertTrue(metrics.getProposalLatency().getMin() > 0);
    }

