import static org.junit.Assert.*;

import java.util.concurrent.CountDownLatch;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.TimeUnit;
import org.apache.zookeeper.server.ServerStats;
import org.junit.Test;

public class ServerStatsTest {
    private static class TestClass {
        private ServerStats serverStats;

        // This will be synchronized in fixed version
        public void setServerStats(ServerStats serverStats) {
            this.serverStats = serverStats;
        }

        public ServerStats getServerStats() {
            return serverStats;
        }
    }

    @Test
    public void testSetServerStatsThreadSafety() throws InterruptedException {
        final TestClass testObj = new TestClass();
        final int threadCount = 10;
        final CountDownLatch latch = new CountDownLatch(1);
        ExecutorService executor = Executors.newFixedThreadPool(threadCount);

        // Create unique ServerStats objects for each thread
        ServerStats[] statsArray = new ServerStats[threadCount];
        for (int i = 0; i < threadCount; i++) {
            statsArray[i] = new ServerStats(null);
        }

        // Submit tasks that will wait for signal to proceed
        for (int i = 0; i < threadCount; i++) {
            final ServerStats stats = statsArray[i];
            executor.submit(() -> {
                try {
                    latch.await();
                    testObj.setServerStats(stats);
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                }
            });
        }

        // Release all threads at once
        latch.countDown();
        executor.shutdown();
        executor.awaitTermination(1, TimeUnit.SECONDS);

        // Verify only one value was set (thread-safe)
        ServerStats result = testObj.getServerStats();
        boolean found = false;
        for (ServerStats s : statsArray) {
            if (s == result) {
                found = true;
                break;
            }
        }
        assertTrue("ServerStats was not set by any thread", found);
    }
}

