import java.util.Random;
import java.util.HashSet;
import java.util.Set;
import java.util.concurrent.CountDownLatch;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

public class RandomInstanceTest {

    // Test that verifies Random instances don't produce duplicate seeds in multi-threaded environment
    @Test
    public void testRandomSeedUniqueness() throws InterruptedException {
        final int threadCount = 100;
        final Set<Long> seeds = new HashSet<>();
        final ExecutorService executor = Executors.newFixedThreadPool(threadCount);
        final CountDownLatch latch = new CountDownLatch(threadCount);

        for (int i = 0; i < threadCount; i++) {
            executor.submit(() -> {
                Random random = new Random(); // Should pass with fixed code
                // Random random = new Random(System.nanoTime()); // Should fail with buggy code
                synchronized (seeds) {
                    seeds.add(random.nextLong());
                }
                latch.countDown();
            });
        }

        latch.await();
        executor.shutdown();

        // With proper seeding, we should have all unique values
        // With System.nanoTime() seeding, collisions are likely in multi-threaded environment
        assert seeds.size() == threadCount : 
            "Expected " + threadCount + " unique random values but got " + seeds.size();
    }
}

