            Set<Watcher> list = existWatches.remove(clientPath);
            if (list != null) {
                // In buggy version: addTo(existWatches.remove(clientPath), result)
                // In fixed version: addTo(list, result)
                materialized.addAll(list); // Simplified version of addTo
            }
        }

        // Assert no NPE occurred and result is empty
        assertTrue(materialized.isEmpty());
