        } catch (Exception e) {
            fail("Should not throw exception for valid ACL string with multiple colons");
        }
    }

    // Helper method matching the signature from the patch
    private static List<ACL> parseACLs(String aclString) {
        // Simplified implementation that shows the key difference being tested
        String[] acls = aclString.split(",");
        List<ACL> aclList = new java.util.ArrayList<ACL>();

        for (String a : acls) {
            int firstColon = a.indexOf(':');
            int lastColon = a.lastIndexOf(':'); // This is the fixed line

            if (firstColon == -1 || lastColon == -1 || firstColon == lastColon) {
                throw new IllegalArgumentException("Invalid ACL format");
            }

            // Simplified ACL creation for test purposes
            ACL acl = new ACL();
            acl.setId(new org.apache.zookeeper.data.Id(
                a.substring(0, firstColon), 
                a.substring(firstColon + 1, lastColon)
            ));
            aclList.add(acl);
        }
        return aclList;
    }
