TEST_CASE:
import org.apache.zookeeper.server.ZooKeeperServer;
import org.apache.zookeeper.server.SessionTracker;
import org.apache.zookeeper.server.FollowerSessionTracker;
import org.junit.Test;
import java.util.HashMap;
import java.util.Map;
import static org.junit.Assert.*;

public class FollowerSessionTrackerTest {

    @Test
    public void testSessionIdGenerationWithServerId() {
        // Setup test data
        long serverId = 12345L;
        Map<Long, Integer> sessionsWithTimeouts = new HashMap<>();
        ZooKeeperServer zks = new ZooKeeperServer() {
            @Override
            public long getServerId() {
                return serverId;
            }
        };

        // Create tracker with serverId (fixed version)
        SessionTracker tracker = new FollowerSessionTracker(zks, sessionsWithTimeouts, serverId);

        // Generate a session ID
        long sessionId = tracker.createSession(30000);

        // Verify the session ID contains the server ID in its bits
        // The server ID should be in the lower 40 bits of the session ID
        long extractedServerId = sessionId & 0x000000FFFFFFFFFFL;
        assertEquals("Session ID should contain server ID", serverId, extractedServerId);
    }

    @Test(expected = NullPointerException.class)
    public void testBuggyVersionFailsWithoutServerId() {
        // Setup test data
        Map<Long, Integer> sessionsWithTimeouts = new HashMap<>();
        ZooKeeperServer zks = new ZooKeeperServer();

        // This should fail with NPE in buggy version since serverId is not passed
        // and the implementation tries to use it for session ID generation
        new FollowerSessionTracker(zks, sessionsWithTimeouts);
    }
}
