import org.junit.Test;
import java.util.Arrays;
import java.util.List;
import static org.junit.Assert.*;

public class AwsSdkPrefixTest {

    @Test
    public void testAwsSdkPrefixMatching() {
        // Test data - classes that should be matched by the prefix
        List<String> testClasses = Arrays.asList(
            "software/amazon/awssdk/services/s3/S3Client",
            "software/amazon/awssdk/core/client/config/ClientOverrideConfiguration",
            "software/amazon/otherpackage/SomeClass"
        );

        // Old buggy prefix would fail to match the third class
        String buggyPrefix = "software/amazon/awssdk";
        assertFalse("Buggy prefix should not match third class", 
            testClasses.get(2).startsWith(buggyPrefix));

        // Fixed prefix should match all classes under software/amazon/
        String fixedPrefix = "software/amazon/";
        assertTrue("Fixed prefix should match first class", 
            testClasses.get(0).startsWith(fixedPrefix));
        assertTrue("Fixed prefix should match second class", 
            testClasses.get(1).startsWith(fixedPrefix));
        assertTrue("Fixed prefix should match third class", 
            testClasses.get(2).startsWith(fixedPrefix));
    }

    @Test
    public void testPrefixEdgeCases() {
        String fixedPrefix = "software/amazon/";

        // Test exact match
        assertTrue("Should match exact prefix",
            "software/amazon/".startsWith(fixedPrefix));

        // Test non-matching cases
        assertFalse("Should not match different prefix",
            "software/othervendor/".startsWith(fixedPrefix));
        assertFalse("Should not match shorter string",
            "software/amazo".startsWith(fixedPrefix));
    }
}

