import org.apache.hadoop.fs.FileSystem;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.mockito.Mock;
import org.mockito.junit.MockitoJUnitRunner;

import static org.apache.hadoop.fs.s3a.S3ATestUtils.assumeStoreAwsHosted;
import static org.mockito.Mockito.when;
import static org.junit.Assume.assumeTrue;

@RunWith(MockitoJUnitRunner.class)
public class S3AStoreHostingTest {

    @Mock
    private FileSystem mockFs;

    @Mock
    private org.apache.hadoop.conf.Configuration mockConf;

    @Test
    public void testAssumeStoreAwsHostedWithAwsEndpoint() {
        // Setup mock to return AWS endpoint
        when(mockFs.getConf()).thenReturn(mockConf);
        when(mockConf.getTrimmed("fs.s3a.endpoint", "s3.amazonaws.com"))
            .thenReturn("s3.amazonaws.com");

        // This should pass with fixed code, fail with buggy code
        // because buggy code had incorrect negation (!)
        assumeStoreAwsHosted(mockFs);

        // If we get here, the test passes (fixed code)
        // With buggy code, the assume would fail and skip the test
        assumeTrue(true);
    }

    @Test
    public void testAssumeStoreAwsHostedWithNonAwsEndpoint() {
        // Setup mock to return non-AWS endpoint
        when(mockFs.getConf()).thenReturn(mockConf);
        when(mockConf.getTrimmed("fs.s3a.endpoint", "s3.amazonaws.com"))
            .thenReturn("some.other.endpoint.com");

        // This should be skipped in both cases, but for different reasons:
        // - Buggy code: would pass the assume (incorrectly)
        // - Fixed code: would skip properly
        try {
            assumeStoreAwsHosted(mockFs);
            // If we get here with buggy code, the test continues
            // With fixed code, we should never reach here
            throw new AssertionError("Test should have been skipped for non-AWS endpoint");
        } catch (Throwable t) {
            // Expected with fixed code - test gets skipped
        }
    }
}

