import org.apache.hadoop.fs.FileUtil;
import org.apache.hadoop.fs.Path;
import org.apache.hadoop.fs.throttled.ThrottledInputStream;
import org.junit.Test;
import org.junit.Rule;
import org.junit.rules.TemporaryFolder;
import static org.junit.Assert.*;

import java.io.File;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.OutputStream;

public class TestThrottledInputStreamPatch {

    @Rule
    public TemporaryFolder tempFolder = new TemporaryFolder();

    @Test
    public void testBandwidthAtLimit() throws IOException {
        // Create a test file with some content
        File testFile = tempFolder.newFile("test.txt");
        try (OutputStream out = new FileOutputStream(testFile)) {
            out.write(new byte[1024 * 1024]); // 1MB file
        }

        // Set bandwidth limit exactly equal to the test rate
        long bandwidthLimit = 1024 * 1024; // 1MB/s
        File outFile = tempFolder.newFile("output.txt");

        // Create a throttled input stream with the exact limit
        ThrottledInputStream in = new ThrottledInputStream(
            testFile.toPath(), bandwidthLimit);

        // Copy the file - should complete in exactly 1 second at full bandwidth
        FileUtil.copy(in, outFile, false, null);

        // This will fail on buggy code (assertTrue(bytesPerSec < maxBPS))
        // but pass on fixed code (assertTrue(bytesPerSec <= maxBPS))
        long bytesPerSec = in.getBytesPerSec();
        assertTrue("Bytes per second should be <= bandwidth limit",
                   bytesPerSec <= bandwidthLimit);
    }
}

