import org.apache.hadoop.yarn.api.records.NodeAttribute;
import org.apache.hadoop.yarn.api.records.impl.pb.AttributeValuePBImpl;
import org.junit.Test;

import java.util.ArrayList;
import java.util.List;
import java.util.concurrent.ConcurrentHashMap;
import java.util.concurrent.CountDownLatch;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

import static org.junit.Assert.assertEquals;

public class HostTest {

    @Test
    public void testConcurrentAttributeUpdates() throws InterruptedException {
        final Host host = new Host("test-host");
        final int numThreads = 10;
        final int updatesPerThread = 100;
        final ExecutorService executor = Executors.newFixedThreadPool(numThreads);
        final CountDownLatch latch = new CountDownLatch(numThreads);

        List<Runnable> tasks = new ArrayList<>();
        for (int i = 0; i < numThreads; i++) {
            final int threadId = i;
            tasks.add(() -> {
                try {
                    for (int j = 0; j < updatesPerThread; j++) {
                        NodeAttribute attr = NodeAttribute.newInstance(
                            "test-domain", "attr-" + threadId + "-" + j, NodeAttribute.PREFIX);
                        AttributeValuePBImpl value = new AttributeValuePBImpl();
                        value.setValue("value-" + threadId + "-" + j);
                        host.getAttributes().put(attr, value);
                    }
                } finally {
                    latch.countDown();
                }
            });
        }

        tasks.forEach(executor::submit);
        latch.await();

        // Verify all updates were processed without concurrent modification exceptions
        assertEquals(numThreads * updatesPerThread, host.getAttributes().size());
    }
}

