import org.junit.Test;
import static org.junit.Assert.*;

public class RouterSafemodeTest {

    private static final long STALE_INTERVAL = 1000L;

    @Test
    public void testCacheStaleCheckWithZeroUpdateTime() {
        // Test case where cacheUpdateTime is 0 (initial/uninitialized state)
        long now = System.currentTimeMillis();
        long cacheUpdateTime = 0L;

        // Should be considered stale when cacheUpdateTime is 0
        boolean isCacheStale = (cacheUpdateTime == 0) || (now - cacheUpdateTime) > STALE_INTERVAL;

        assertTrue("Cache should be considered stale when update time is 0", isCacheStale);
    }

    @Test
    public void testCacheStaleCheckWithRecentUpdate() {
        // Test case where cache was recently updated
        long now = System.currentTimeMillis();
        long cacheUpdateTime = now - 500L; // Within stale interval

        boolean isCacheStale = (cacheUpdateTime == 0) || (now - cacheUpdateTime) > STALE_INTERVAL;

        assertFalse("Cache should not be stale when recently updated", isCacheStale);
    }

    @Test
    public void testCacheStaleCheckWithStaleUpdate() {
        // Test case where cache is actually stale
        long now = System.currentTimeMillis();
        long cacheUpdateTime = now - 2000L; // Beyond stale interval

        boolean isCacheStale = (cacheUpdateTime == 0) || (now - cacheUpdateTime) > STALE_INTERVAL;

        assertTrue("Cache should be stale when beyond update interval", isCacheStale);
    }
}

