import org.junit.Test;
import static org.junit.Assert.*;
import static org.mockito.Mockito.*;
import java.io.IOException;

public class BucketLocationTest {

    @Test
    public void testGetBucketLocationWithRetry() throws IOException {
        // Setup mock invoker that will throw exception on first call
        Invoker mockInvoker = mock(Invoker.class);
        when(mockInvoker.retry(anyString(), anyString(), anyBoolean(), any()))
            .thenThrow(new IOException("First attempt fails"))
            .thenReturn("us-east-1");

        // Create test object with mock invoker
        TestClass testObj = new TestClass();
        testObj.setInvoker(mockInvoker);

        // This should pass with fixed code (retry succeeds)
        // Would fail with buggy code (once() would fail permanently on first attempt)
        String result = testObj.getBucketLocation("test-bucket");
        assertEquals("us-east-1", result);

        // Verify retry was called with correct parameters
        verify(mockInvoker, times(2)).retry(
            eq("getBucketLocation()"),
            eq("test-bucket"), 
            eq(true),
            any());
    }

    // Minimal test class to demonstrate the behavior
    static class TestClass {
        private Invoker invoker;
        private String accessPoint = null;

        public void setInvoker(Invoker invoker) {
            this.invoker = invoker;
        }

        public String getBucketLocation(String bucketName) throws IOException {
            return invoker.retry("getBucketLocation()", bucketName, true, () -> {
                if (accessPoint != null) {
                    return accessPoint.getRegion();
                }
                return "us-east-1"; // default
            });
        }
    }

    // Minimal Invoker interface for testing
    interface Invoker {
        <T> T retry(String operation, String path, boolean idempotent, Invocation<T> invocation) 
            throws IOException;
    }

    @FunctionalInterface
    interface Invocation<T> {
        T execute() throws IOException;
    }
}

