import static org.junit.Assert.*;
import org.junit.Test;

public class ProtobufHelperTest {

    @Test
    public void testVerifyCauseReturnsCorrectException() {
        // Create a test exception with a specific cause
        RuntimeException rootCause = new RuntimeException("Root cause");
        Exception wrapperException = new Exception("Wrapper", rootCause);

        // Test that verifyCause returns the cause (rootCause) not the caught exception (wrapperException)
        RuntimeException result = ShadedProtobufHelper.verifyCause(
            RuntimeException.class,
            wrapperException
        );

        // This assertion will:
        // - FAIL on buggy code (would return wrapperException)
        // - PASS on fixed code (returns rootCause)
        assertSame("Should return the cause, not the caught exception", 
                  rootCause, result);
    }

    // Mock implementation of the helper class to test the exact patch behavior
    static class ShadedProtobufHelper {
        public static <E extends Throwable> E verifyCause(Class<E> clazz, Throwable caught) {
            Throwable cause = caught.getCause();
            if (cause == null || !clazz.isAssignableFrom(cause.getClass())) {
                throw caught;
            } else {
                // This is the line that was patched - changing from caught to cause
                return (E) cause;  // Fixed version
                // return (E) caught;  // Buggy version
            }
        }
    }
}

