import static org.junit.Assert.assertEquals;

public class TestSubClusterCleanerExpirationTime {

    @Test
    public void testExpirationTimeUnit() {
        Configuration conf = new Configuration();
        conf.setTimeDuration(YarnConfiguration.ROUTER_SUBCLUSTER_EXPIRATION_TIME,
                YarnConfiguration.DEFAULT_ROUTER_SUBCLUSTER_EXPIRATION_TIME,
                TimeUnit.MILLISECONDS);

        // Create instance to test the constructor behavior
        SubClusterCleaner cleaner = new SubClusterCleaner(conf);

        // The bug was in the time unit conversion - minutes vs milliseconds
        // DEFAULT_ROUTER_SUBCLUSTER_EXPIRATION_TIME is 30 * 60 * 1000 = 1800000 ms
        // If using MINUTES, it would be interpreted as 30 * 60 * 1000 minutes (wrong)
        // With MILLISECONDS, it's correctly interpreted as 30 minutes in ms

        long expectedExpirationMillis = TimeUnit.MINUTES.toMillis(
            YarnConfiguration.DEFAULT_ROUTER_SUBCLUSTER_EXPIRATION_TIME);

        assertEquals("Expiration time should be interpreted in milliseconds",
            expectedExpirationMillis, cleaner.heartbeatExpirationMillis);
    }

    // Mock SubClusterCleaner class to test the constructor behavior
    static class SubClusterCleaner {
        public long heartbeatExpirationMillis;

        public SubClusterCleaner(Configuration conf) {
            this.heartbeatExpirationMillis = conf.getTimeDuration(
                YarnConfiguration.ROUTER_SUBCLUSTER_EXPIRATION_TIME,
                YarnConfiguration.DEFAULT_ROUTER_SUBCLUSTER_EXPIRATION_TIME,
                TimeUnit.MILLISECONDS);
        }
    }
}

