import org.apache.hadoop.fs.permission.FsAction;
import org.apache.hadoop.fs.permission.FsPermission;
import org.apache.hadoop.hdfs.server.namenode.FSDirectory;
import org.apache.hadoop.hdfs.server.namenode.FSPermissionChecker;
import org.apache.hadoop.security.AccessControlException;
import org.junit.Before;
import org.junit.Test;
import org.mockito.Mockito;

import static org.junit.Assert.assertThrows;
import static org.mockito.Mockito.when;

public class TestConcatPermissionCheck {
    private FSDirectory fsd;
    private FSPermissionChecker pc;
    private String[] srcs = {"/file1"};

    @Before
    public void setup() {
        fsd = Mockito.mock(FSDirectory.class);
        pc = Mockito.mock(FSPermissionChecker.class);

        // Mock path resolution
        INodesInPath iip = Mockito.mock(INodesInPath.class);
        when(fsd.resolvePath(pc, srcs[0], DirOp.WRITE)).thenReturn(iip);
    }

    @Test
    public void testPermissionCheckWhenDisabled() throws Exception {
        // Set permissions to be disabled
        when(fsd.isPermissionEnabled()).thenReturn(false);

        // Should not throw exception even with null permission checker
        verifySrcFiles(fsd, srcs, null);

        // Should not throw exception even with non-null permission checker
        verifySrcFiles(fsd, srcs, pc);

        // Verify no permission checks were made
        Mockito.verify(fsd, Mockito.never()).checkPathAccess(Mockito.any(), Mockito.any(), Mockito.any());
        Mockito.verify(fsd, Mockito.never()).checkParentAccess(Mockito.any(), Mockito.any(), Mockito.any());
    }

    @Test
    public void testPermissionCheckWhenEnabled() throws Exception {
        // Set permissions to be enabled
        when(fsd.isPermissionEnabled()).thenReturn(true);

        // Should throw exception when permission checker is null
        assertThrows(AccessControlException.class, () -> verifySrcFiles(fsd, srcs, null));

        // Should perform permission checks when permission checker exists
        verifySrcFiles(fsd, srcs, pc);
        Mockito.verify(fsd).checkPathAccess(pc, Mockito.any(), Mockito.eq(FsAction.READ));
        Mockito.verify(fsd).checkParentAccess(pc, Mockito.any(), Mockito.eq(FsAction.WRITE));
    }

    // Helper method to call the method under test
    private void verifySrcFiles(FSDirectory fsd, String[] srcs, FSPermissionChecker pc) throws Exception {
        try {
            // This would normally be called via reflection since it's private
            // For test purposes we're making it accessible
            Method method = FSDirectory.class.getDeclaredMethod(
                "verifySrcFiles", FSDirectory.class, String[].class, FSPermissionChecker.class);
            method.setAccessible(true);
            method.invoke(null, fsd, srcs, pc);
        } catch (InvocationTargetException e) {
            throw (Exception) e.getCause();
        }
    }
}

