import org.apache.hadoop.fs.FSDataOutputStream;
import org.apache.hadoop.fs.Path;
import org.apache.hadoop.hdfs.DistributedFileSystem;
import org.apache.hadoop.hdfs.MiniDFSCluster;
import org.junit.After;
import org.junit.Before;
import org.junit.Test;

import java.io.IOException;

public class TestDFSStripedOutputStreamUpdatePipelineTest {
    private MiniDFSCluster cluster;
    private DistributedFileSystem dfs;
    private static final Path FILE_PATH = new Path("/test/file");

    @Before
    public void setup() throws IOException {
        cluster = new MiniDFSCluster.Builder(new org.apache.hadoop.conf.Configuration())
                .numDataNodes(3)
                .build();
        dfs = cluster.getFileSystem();
    }

    @After
    public void tearDown() {
        if (cluster != null) {
            cluster.shutdown();
        }
    }

    @Test(timeout = 60000)
    public void testLoopCondition() throws Exception {
        FSDataOutputStream out = dfs.create(FILE_PATH);
        try {
            // This test verifies the loop doesn't run with Long.MAX_VALUE iterations
            // On buggy code, it would try to run forever (or until timeout)
            // On fixed code, it should complete within reasonable time
            for (int i = 0; i < Integer.MAX_VALUE; i++) {
                out.write(i);
                if (i == 1024 * 1024 * 5) {  // 5MB written
                    cluster.stopDataNode(0);
                    break;  // Exit after testing the condition
                }
            }
        } finally {
            out.close();
        }
    }
}

