import static org.junit.Assert.*;

import org.junit.Test;

public class IdentifierValidationTest {
    private static final String IDENTIFIER_PATTERN_STR = "[a-zA-Z][a-zA-Z0-9]*";

    @Test
    public void testErrorMessageFormat() {
        String name = "testName";
        String value = "123invalid"; // Doesn't match pattern

        try {
            validIdentifier(value, 100, name);
            fail("Expected IllegalArgumentException");
        } catch (IllegalArgumentException e) {
            // Test that the message uses double quotes around pattern
            String expectedMsg = MessageFormat.format(
                "[{0}] = [{1}] must be \"{2}\"", 
                name, value, IDENTIFIER_PATTERN_STR);
            assertEquals(expectedMsg, e.getMessage());

            // Additional check that single quotes would fail (testing buggy version)
            assertFalse(e.getMessage().contains("'" + IDENTIFIER_PATTERN_STR + "'"));
        }
    }

    // Mock implementation that throws exception with the message format we want to test
    private static String validIdentifier(String value, int maxLen, String name) {
        if (!value.matches(IDENTIFIER_PATTERN_STR)) {
            throw new IllegalArgumentException(
                MessageFormat.format(
                    "[{0}] = [{1}] must be \"{2}\"", 
                    name, value, IDENTIFIER_PATTERN_STR));
        }
        return value;
    }
}

