        resourceInfo1[1] = new ResourceWeights(0.3f); // MEM
        resourceInfo2[0] = new ResourceWeights(0.4f); // CPU
        resourceInfo2[1] = new ResourceWeights(0.6f); // MEM

        minShareInfo1[0] = new ResourceWeights(1.0f); // CPU min share
        minShareInfo1[1] = new ResourceWeights(1.0f); // MEM min share
        minShareInfo2[0] = new ResourceWeights(1.0f); // CPU min share
        minShareInfo2[1] = new ResourceWeights(0.5f); // MEM min share (below resource value)

        // Mock the comparator and inject our test data
