import org.junit.Test;
import static org.junit.Assert.*;

public class BlockReportLeaseManagerTest {

    private static class NodeData {
        long leaseTimeMs;
        NodeData(long leaseTimeMs) {
            this.leaseTimeMs = leaseTimeMs;
        }
    }

    @Test
    public void testPruneIfExpired() {
        // Test case where current time is exactly at lease expiry boundary
        long leaseExpiryMs = 10000L;
        long leaseStartTime = 1000L;
        NodeData node = new NodeData(leaseStartTime);

        // Case 1: Exactly at expiry time (1000 + 10000 = 11000)
        long currentTime1 = 11000L;
        // Should return false (not expired) in buggy code, true (expired) in fixed code
        boolean result1 = pruneIfExpiredHelper(currentTime1, node, leaseExpiryMs);

        // Case 2: Just before expiry (buggy and fixed should agree)
        long currentTime2 = 10999L;
        boolean result2 = pruneIfExpiredHelper(currentTime2, node, leaseExpiryMs);

        // Case 3: Just after expiry (buggy and fixed should agree)
        long currentTime3 = 11001L;
        boolean result3 = pruneIfExpiredHelper(currentTime3, node, leaseExpiryMs);

        // Assertions that will fail on buggy code but pass on fixed code
        assertFalse("Lease should be expired at exact expiry time", result1);
        assertFalse("Lease should not be expired just before expiry", result2);
        assertTrue("Lease should be expired just after expiry", result3);
    }

    // Helper method to simulate the pruneIfExpired logic
    private boolean pruneIfExpiredHelper(long monotonicNowMs, NodeData node, long leaseExpiryMs) {
        // This is the buggy version that would fail the test
        // if (monotonicNowMs < node.leaseTimeMs + leaseExpiryMs) {

        // This is the fixed version that passes the test
        if (monotonicNowMs - node.leaseTimeMs < leaseExpiryMs) {
            return false;
        }
        return true;
    }
}

