import org.junit.Test;
import java.io.IOException;
import java.util.concurrent.CountDownLatch;
import java.util.concurrent.TimeUnit;

public class TestRetryProxyTimeout {
    // This test simulates a long-running operation that would timeout with 60s
    // but complete successfully with 100s timeout
    @Test(timeout = 60000)
    public void testLongRunningOperation() throws IOException, InterruptedException {
        // Simulate a race condition by creating multiple threads that hold resources
        final int threadCount = 10;
        final CountDownLatch startLatch = new CountDownLatch(1);
        final CountDownLatch finishLatch = new CountDownLatch(threadCount);

        for (int i = 0; i < threadCount; i++) {
            new Thread(() -> {
                try {
                    startLatch.await();
                    // Simulate work that takes 70 seconds (would timeout with 60s limit)
                    Thread.sleep(70000);
                    finishLatch.countDown();
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                }
            }).start();
        }

        startLatch.countDown();
        // Wait for all threads to complete (would timeout with original 60s limit)
        if (!finishLatch.await(90, TimeUnit.SECONDS)) {
            throw new IOException("Test timed out waiting for threads to complete");
        }
    }
}

