        assertTrue(Pattern.matches(fixedRegex, "valid.prop%name"));
        assertFalse(Pattern.matches(fixedRegex, "invalid-prop.name"));
        assertFalse(Pattern.matches(fixedRegex, "1invalid.prop.name"));
    }
}

