        MutableGaugeFloat gauge = new MutableGaugeFloat("test", "test", value);

        // This would cause infinite loop in buggy version because:
        // 1. value.get() returns int bits of NaN
        // 2. Adding delta to NaN still gives NaN
        // 3. compareAndSet would keep failing
        gauge.incr(1.0f);

        // In fixed version, it should complete without hanging
        // Verify the value was updated (though exact value doesn't matter for this test)
        assertNotEquals(Float.NaN, Float.intBitsToFloat(value.get()));
    }

    // Minimal implementation needed for the test
    static class MutableGaugeFloat {
        private final AtomicInteger value;

        public MutableGaugeFloat(String name, String description, AtomicInteger value) {
            this.value = value;
        }

        private boolean compareAndSet(float expect, float update) {
            int expectBits = Float.floatToIntBits(expect);
            int updateBits = Float.floatToIntBits(update);
            return value.compareAndSet(expectBits, updateBits);
        }

        void incr(float delta) {
            while (true) {
                float current = Float.intBitsToFloat(value.get());
                float next = current + delta;
                if (compareAndSet(current, next)) {
                    break;
                }
            }
        }

        void setChanged() {
            // dummy implementation
        }
    }
