import static org.junit.Assert.assertEquals;
import static org.junit.Assert.fail;

public class TestRMAdminCLIRetryLogic {

    interface UnreliableInterface {
        void failsOnceWithSaslException() throws SaslException;
    }

    @Test
    public void testRetryOtherThanRemoteAndSaslException() throws Throwable {
        final AtomicInteger callCount = new AtomicInteger(0);
        UnreliableInterface unreliableImpl = new UnreliableInterface() {
            @Override
            public void failsOnceWithSaslException() throws SaslException {
                if (callCount.getAndIncrement() == 0) {
                    throw new SaslException("Test SASL failure");
                }
            }
        };

        Map<Class<? extends Exception>, RetryPolicy> exceptionToPolicyMap =
            Collections.emptyMap();

        UnreliableInterface unreliable = (UnreliableInterface) RetryProxy.create(
            UnreliableInterface.class,
            unreliableImpl,
            RetryProxy.retryOtherThanRemoteAndSaslException(
                RetryProxy.TRY_ONCE_THEN_FAIL,
                exceptionToPolicyMap));

        try {
            unreliable.failsOnceWithSaslException();
            assertEquals("Should have retried once", 2, callCount.get());
        } catch (SaslException e) {
            // On buggy code (retryOtherThanRemoteException), this will be thrown
            // On fixed code, it should retry and succeed
            fail("Should have retried on SaslException");
        }
    }
}

