import org.apache.hadoop.conf.Configuration;
import org.apache.hadoop.io.retry.RetryPolicy;
import org.apache.hadoop.io.retry.RetryPolicies;
import org.apache.hadoop.security.SaslException;
import org.junit.Test;

import java.io.IOException;
import java.util.HashMap;
import java.util.Map;

import static org.junit.Assert.*;

public class RMAdminCLIRetryPolicyTest {

    @Test
    public void testRetryPolicyForSaslException() throws Exception {
        Configuration conf = new Configuration();
        Map<Class<? extends Exception>, RetryPolicy> exceptionToPolicyMap = new HashMap<>();

        // Create the retry policy using the method under test
        RetryPolicy policy = createRetryPolicy(conf, exceptionToPolicyMap);

        // Test that SaslException is NOT retried (should return false for shouldRetry)
        SaslException saslException = new SaslException("SASL failure");
        assertFalse("SaslException should not be retried", 
            policy.shouldRetry(saslException, 0, 0, false));
    }

    // Helper method that mimics the patched method's behavior
    private RetryPolicy createRetryPolicy(Configuration conf, 
            Map<Class<? extends Exception>, RetryPolicy> exceptionToPolicyMap) {
        // This is the key change in the patch - using retryOtherThanRemoteAndSaslException
        return RetryPolicies.retryOtherThanRemoteAndSaslException(
            RetryPolicies.TRY_ONCE_THEN_FAIL, exceptionToPolicyMap);
    }

    @Test
    public void testRetryPolicyForRemoteException() throws Exception {
        Configuration conf = new Configuration();
        Map<Class<? extends Exception>, RetryPolicy> exceptionToPolicyMap = new HashMap<>();

        RetryPolicy policy = createRetryPolicy(conf, exceptionToPolicyMap);

        // Test that IOException (remote) is NOT retried
        IOException ioException = new IOException("Remote failure");
        assertFalse("Remote IOException should not be retried",
            policy.shouldRetry(ioException, 0, 0, false));
    }

    @Test
    public void testRetryPolicyForOtherExceptions() throws Exception {
        Configuration conf = new Configuration();
        Map<Class<? extends Exception>, RetryPolicy> exceptionToPolicyMap = new HashMap<>();

        RetryPolicy policy = createRetryPolicy(conf, exceptionToPolicyMap);

        // Test that other exceptions ARE retried
        Exception otherException = new Exception("Other failure");
        assertTrue("Non-SASL, non-remote exceptions should be retried",
            policy.shouldRetry(otherException, 0, 0, false));
    }
}

