    public void setup() throws Exception {
        // Setup a minimal cluster with 2 NameNodes
        dfsCluster = new MiniDFSCluster.Builder(new Configuration())
            .nnTopology(MiniDFSNNTopology.simpleHATopology())
            .numDataNodes(1)
            .build();
        dfsCluster.waitActive();
