        // The buggy version with restartNameNode(0, false) would fail
        // The fixed version with restartNameNode(0) should pass

        // Get initial state
        boolean nn0WasActive = dfsCluster.getNameNode(0).isActiveState();
        boolean nn1WasActive = dfsCluster.getNameNode(1).isActiveState();

        try {
            // This would fail with the buggy version
            dfsCluster.restartNameNode(0);

            // Verify the NameNode came back up
            assertNotNull("NameNode 0 should be running", dfsCluster.getNameNode(0));
            assertTrue("NameNode 0 should be in a valid state", 
                dfsCluster.getNameNode(0).isActiveState() || dfsCluster.getNameNode(0).isStandbyState());

            // Verify the other NameNode wasn't affected
            assertNotNull("NameNode 1 should still be running", dfsCluster.getNameNode(1));
            assertEquals("NameNode 1 state should be unchanged", 
                nn1WasActive, dfsCluster.getNameNode(1).isActiveState());
        } finally {
            // Clean up
            dfsCluster.restartNameNode(1);
        }
    }

    @After
    public void tearDown() throws Exception {
        if (dfsCluster != null) {
            dfsCluster.shutdown();
        }
    }
}

