public class PendingReconstructionTest {
    class PendingBlockInfo {
        List<Object> targets = new ArrayList<>();
    }

    @Test
    public void testGetTargetsThreadSafety() throws InterruptedException {
        // Setup test data
        PendingBlockInfo found = new PendingBlockInfo();
        found.targets.add("target1");
        found.targets.add("target2");

        // Get the original list reference
        List<Object> originalList = found.targets;

        // Simulate concurrent modification
        Thread modifier = new Thread(() -> {
            originalList.add("target3");
        });

        // Test the buggy version - should throw ConcurrentModificationException
        try {
            modifier.start();
            List<Object> result = originalList; // This simulates the buggy return
            modifier.join();

            // Try to iterate the returned list while it's being modified
            for (Object o : result) {
                // Just reading the elements
            }
            fail("Expected ConcurrentModificationException but none was thrown");
        } catch (ConcurrentModificationException e) {
            // Expected with buggy code
        }

        // Reset for fixed version test
        found.targets = new ArrayList<>(originalList);
        modifier = new Thread(() -> {
            originalList.add("target4"); // Modify original list
        });

        // Test the fixed version - should not throw
        try {
            modifier.start();
            List<Object> result = new ArrayList<>(found.targets); // This simulates the fixed return
            modifier.join();

            // Iterate the copied list - should not throw
            for (Object o : result) {
                // Just reading the elements
            }
        } catch (ConcurrentModificationException e) {
            fail("Fixed version threw ConcurrentModificationException");
        }
    }
}

