public class HdfsFileStatusTest {

    @Test
    public void testBuildWithDirectoryAndNullLocations() {
        // Create a builder for a directory with null locations
        HdfsFileStatus.Builder builder = new HdfsFileStatus.Builder()
                .length(100)
                .isdir(true)  // This is the key condition being tested
                .replication(3)
                .blocksize(128)
                .mtime(System.currentTimeMillis())
                .atime(System.currentTimeMillis())
                .permission(new FsPermission("755"))
                .owner("owner")
                .group("group")
                .path("testDir")
                .fileId(12345L)
                .childrenNum(5)
                .locations(null);  // Null locations

        // This should pass with the fixed code (isdir=true condition)
        // but fail with buggy code (isdir=false condition)
        HdfsFileStatus status = builder.build();

        assertNotNull(status);
        assertTrue(status.isDir());
        assertNull(status.getLocations());
    }

    @Test
    public void testBuildWithFileAndNullLocations() {
        // Create a builder for a file with null locations
        HdfsFileStatus.Builder builder = new HdfsFileStatus.Builder()
                .length(100)
                .isdir(false)  // Not a directory
                .replication(3)
                .blocksize(128)
                .mtime(System.currentTimeMillis())
                .atime(System.currentTimeMillis())
                .permission(new FsPermission("644"))
                .owner("owner")
                .group("group")
                .path("testFile")
                .fileId(12345L)
                .locations(null);  // Null locations

        // This should work in both cases since the condition is about directories
        HdfsFileStatus status = builder.build();

        assertNotNull(status);
        assertFalse(status.isDir());
        assertNull(status.getLocations());
    }

    @Test
    public void testBuildWithDirectoryAndLocations() {
        LocatedBlocks mockLocations = mock(LocatedBlocks.class);

        // Create a builder for a directory with locations
        HdfsFileStatus.Builder builder = new HdfsFileStatus.Builder()
                .length(100)
                .isdir(true)
                .replication(3)
                .blocksize(128)
                .mtime(System.currentTimeMillis())
                .atime(System.currentTimeMillis())
                .permission(new FsPermission("755"))
                .owner("owner")
                .group("group")
                .path("testDir")
                .fileId(12345L)
                .childrenNum(5)
                .locations(mockLocations);  // Non-null locations

        // This should work in both cases since locations is not null
        HdfsFileStatus status = builder.build();

        assertNotNull(status);
        assertTrue(status.isDir());
        assertSame(mockLocations, status.getLocations());
    }
}

