import org.apache.hadoop.fs.permission.FsPermission;
import org.apache.hadoop.hdfs.protocol.HdfsFileStatus;
import org.apache.hadoop.hdfs.server.federation.resolver.RemoteLocation;
import org.junit.Before;
import org.junit.Test;
import org.mockito.Mock;
import org.mockito.MockitoAnnotations;

import java.io.IOException;
import java.util.ArrayList;
import java.util.List;

import static org.junit.Assert.*;
import static org.mockito.Mockito.*;

public class TestCreateOperationLocation {

    @Mock
    private RouterRpcServer rpcServer;
    @Mock
    private RouterRpcClient rpcClient;

    private RouterFederationProtocol protocol;
    private String testPath = "/test/path";
    private FsPermission testPermission = new FsPermission("755");
    private List<RemoteLocation> testLocations = new ArrayList<>();

    @Before
    public void setup() {
        MockitoAnnotations.initMocks(this);
        protocol = new RouterFederationProtocol(rpcServer, rpcClient);
        testLocations.add(new RemoteLocation("ns1", testPath));
    }

    @Test
    public void testCreateOperationUsesLocations() throws IOException {
        // Setup mock behavior
        when(rpcServer.getLocationsForPath(testPath, true)).thenReturn(testLocations);
        RemoteLocation expectedLocation = new RemoteLocation("ns1", testPath);
        when(rpcServer.getCreateLocation(testPath, testLocations)).thenReturn(expectedLocation);
        HdfsFileStatus mockStatus = mock(HdfsFileStatus.class);
        when(rpcClient.invokeSingle(expectedLocation, any(), eq(HdfsFileStatus.class)))
            .thenReturn(mockStatus);

        // Execute test
        HdfsFileStatus result = protocol.create(testPath, testPermission, false);

        // Verify behavior
        verify(rpcServer).getLocationsForPath(testPath, true);
        verify(rpcServer).getCreateLocation(testPath, testLocations);
        assertEquals(mockStatus, result);
    }

    @Test(expected = NullPointerException.class)
    public void testBuggyCodeFailsWithoutLocations() throws IOException {
        // Setup mock behavior that would trigger the bug
        when(rpcServer.getLocationsForPath(testPath, true)).thenReturn(testLocations);
        when(rpcServer.getCreateLocation(testPath)).thenThrow(new NullPointerException());

        // This should throw NPE in buggy version
        protocol.create(testPath, testPermission, false);
    }

    // Mock class to test against
    private static class RouterFederationProtocol {
        private final RouterRpcServer rpcServer;
        private final RouterRpcClient rpcClient;

        public RouterFederationProtocol(RouterRpcServer rpcServer, RouterRpcClient rpcClient) {
            this.rpcServer = rpcServer;
            this.rpcClient = rpcClient;
        }

        public HdfsFileStatus create(String src, FsPermission masked, boolean createParent) 
            throws IOException {
            List<RemoteLocation> locations = rpcServer.getLocationsForPath(src, true);
            RemoteLocation createLocation = null;
            try {
                // This line changes between buggy and fixed versions
                createLocation = rpcServer.getCreateLocation(src, locations);
                return rpcClient.invokeSingle(createLocation, null, HdfsFileStatus.class);
            } catch (IOException ioe) {
                throw ioe;
            }
        }
    }

    // Mock interfaces
    private interface RouterRpcServer {
        List<RemoteLocation> getLocationsForPath(String src, boolean isCreate);
        RemoteLocation getCreateLocation(String src);
        RemoteLocation getCreateLocation(String src, List<RemoteLocation> locations);
    }

    private interface RouterRpcClient {
        <T> T invokeSingle(RemoteLocation location, Object method, Class<T> clazz) 
            throws IOException;
    }
}

