import org.junit.Test;
import static org.junit.Assert.*;
import java.util.concurrent.ConcurrentHashMap;
import java.util.concurrent.ConcurrentMap;
import java.util.concurrent.atomic.AtomicBoolean;

public class ThrottledAsyncCheckerTest {

    @Test
    public void testOnSuccessWithNonNullResult() {
        // Setup test objects
        ConcurrentMap<Object, Object> checksInProgress = new ConcurrentHashMap<>();
        ConcurrentMap<Object, Object> completedChecks = new ConcurrentHashMap<>();
        Object target = new Object();
        String testResult = "test-result";

        checksInProgress.put(target, new Object());

        // Create anonymous implementation of the patched class
        FutureCallback<String> callback = new FutureCallback<String>() {
            @Override
            public void onSuccess(String result) {
                synchronized (this) {
                    checksInProgress.remove(target);
                    completedChecks.put(target, result);
                }
            }

            @Override
            public void onFailure(Throwable t) {
                fail("Should not reach failure case");
            }
        };

        // Test the behavior
        callback.onSuccess(testResult);

        // Verify the results
        assertFalse("Target should be removed from checksInProgress", 
                   checksInProgress.containsKey(target));
        assertEquals("Result should be stored in completedChecks",
                   testResult, completedChecks.get(target));
    }

    // Minimal interface needed for the test
    interface FutureCallback<V> {
        void onSuccess(V result);
        void onFailure(Throwable t);
    }
}

