public class QueueAppNumTest {
    private TestClass testClass;

    // Wrapper class to test the increment behavior
    private static class TestClass {
        private Integer appNum = null;

        public void increaseQueueAppNum() {
            if (appNum == null) {
                appNum = 1;
            } else {
                appNum = appNum + 1;  // This is the fixed version
                // appNum++;         // This would be the buggy version
            }
        }

        public Integer getAppNum() {
            return appNum;
        }
    }

    @Before
    public void setUp() {
        testClass = new TestClass();
    }

    @Test
    public void testAppNumIncrement() {
        // First call should initialize to 1
        testClass.increaseQueueAppNum();
        assertEquals(Integer.valueOf(1), testClass.getAppNum());

        // Second call should increment to 2
        testClass.increaseQueueAppNum();
        assertEquals(Integer.valueOf(2), testClass.getAppNum());

        // Third call should increment to 3
        testClass.increaseQueueAppNum();
        assertEquals(Integer.valueOf(3), testClass.getAppNum());
    }

    @Test
    public void testAppNumIncrementAfterMaxValue() {
        testClass.appNum = Integer.MAX_VALUE - 1;

        // First increment should go to MAX_VALUE
        testClass.increaseQueueAppNum();
        assertEquals(Integer.valueOf(Integer.MAX_VALUE), testClass.getAppNum());

        // Next increment should wrap around to MIN_VALUE (tests post-increment vs compound assignment)
        testClass.increaseQueueAppNum();
        assertEquals(Integer.valueOf(Integer.MIN_VALUE), testClass.getAppNum());
    }
}

