import com.fasterxml.jackson.databind.ObjectMapper;
import com.fasterxml.jackson.databind.ObjectWriter;
import org.junit.Test;

import static org.junit.Assert.*;

public class JsonUtilsTest {

    @Test
    public void testWriterConfiguration() {
        ObjectMapper mapper = new ObjectMapper();

        // Configure visibility as in the original code
        mapper.setVisibility(
            mapper.getSerializationConfig()
                .getDefaultVisibilityChecker()
                .withFieldVisibility(JsonAutoDetect.Visibility.ANY)
                .withGetterVisibility(JsonAutoDetect.Visibility.NONE)
        );

        // Test the writer configuration
        ObjectWriter writer = mapper.writerWithDefaultPrettyPrinter();
        ObjectWriter defaultWriter = mapper.writer();

        // The test should fail on buggy code where WRITER = mapper.writer()
        // and pass on fixed code where WRITER = mapper.writerWithDefaultPrettyPrinter()
        assertNotSame("Writer should be configured with pretty printer", 
                     defaultWriter, writer);

        // Additional check that the pretty printer is actually configured
        assertTrue("Writer should be pretty printing writer",
                  writer.toString().contains("PrettyPrinter"));
    }
}

