        public String toString() {
            return value;
        }
    }

    @Test
    public void testJsonStringConversion() {
        // Create a test token with JSON-like content
        TestToken token = new TestToken("{\"key\":\"value\"}");

        // In buggy version: JsonUtils.toJsonString(token.toString()) would double-encode
        // In fixed version: token.toString() returns the proper JSON string directly

        // Expected behavior after fix: should return the original JSON string
        String expected = "{\"key\":\"value\"}";

        // This test will:
        // - FAIL on buggy code (would return "\"{\\\"key\\\":\\\"value\\\"}\"")
        // - PASS on fixed code (returns original JSON string)
        assertEquals(expected, token.toString());
    }

    @Test
    public void testMaliciousJsonInjection() {
        // Test with potentially malicious JSON content
        TestToken token = new TestToken("\"}); maliciousCode(); //");

        // In buggy version: would double-encode and potentially create injection vulnerability
        // In fixed version: returns the string as-is

        // Expected behavior after fix: should return the original string without modification
        String expected = "\"}); maliciousCode(); //";

        // This test will:
        // - FAIL on buggy code (would return escaped version)
        // - PASS on fixed code (returns original string)
        assertEquals(expected, token.toString());
    }
}

