TEST_CASE:
import static org.junit.Assert.assertEquals;
import static org.junit.Assert.assertThrows;

import org.junit.Test;

public class ParameterReplacementTest {

    @Test
    public void testParameterBoundaryCheck() {
        // Test case where num equals params.length (should throw in fixed version)
        BadFormatString exception = assertThrows(BadFormatString.class,
            () -> replaceParameters("test $0", new String[]{"param"}, "1"));

        // Verify the exception message contains correct boundary info
        assertEquals("index 1 from test $0 is outside of the valid range 0 to 0", 
            exception.getMessage());
    }

    @Test
    public void testValidParameterIndex() {
        // Test valid case (num < params.length)
        String result = replaceParameters("test $0", new String[]{"param"}, "0");
        assertEquals("test param", result);
    }

    // Helper method to simulate the behavior being tested
    private String replaceParameters(String format, String[] params, String paramNum) {
        if (paramNum != null) {
            try {
                int num = Integer.parseInt(paramNum);
                if (num < 0 || num >= params.length) {  // This line was patched
                    throw new BadFormatString("index " + num + " from " + format + 
                        " is outside of the valid range 0 to " + (params.length - 1));
                }
                return format.replace("$" + paramNum, params[num]);
            } catch (NumberFormatException e) {
                throw new BadFormatString("Invalid parameter number: " + paramNum);
            }
        }
        return format;
    }

    // Simple exception class to match the behavior
    private static class BadFormatString extends RuntimeException {
        public BadFormatString(String message) {
            super(message);
        }
    }
}

