                    }
                } catch (Exception e) {
                    failed.set(true);
                } finally {
                    endLatch.countDown();
                }
            }).start();
        }

        // Reset client before test
        CosmosDBDocumentStoreWriter.client = null;

        // Start all threads at once
        startLatch.countDown();

        // Wait for all threads to complete
        endLatch.await();

        assertFalse("Thread safety violation detected", failed.get());
    }

