        String pathOnly = PathUtils.getTestDir(getClass()).getPath();

        // Create paths from both versions
        Path pathFromAbsolute = new Path(absolutePath, "testfile");
        Path pathFromRelative = new Path(pathOnly, "testfile");

        // On Windows, absolute paths start with drive letter (e.g., C:\) 
        // while relative paths don't. The fixed version should handle both cases.
        if (System.getProperty("os.name").toLowerCase().contains("win")) {
            // This will fail on buggy code because getAbsolutePath() adds Windows drive prefix
            // which may cause issues when combined with Path constructor
            assertFalse("Path should not start with drive letter when using getPath()",
                       pathFromRelative.toString().matches("^[A-Za-z]:\\\\.*"));

            // Verify the fixed version produces expected path format
            assertTrue("Path should maintain relative format",
                      pathFromRelative.toString().startsWith(pathOnly));
        }
    }
}

