        String containerPath = "/test/container/path";
        String emptyString = "";
        String hostName = "test-host";

        // Expected format string from the fixed code
        String formatStr = "%s\t%s\t%s\t%s\t%s\t%s\t%s";

        // Buggy format string that would cause test to fail
        String formatStrWithHash = "%s\t%s\t%s\t%s\t%s\t%s\t%s\t%s";

        // Expected correct output
        String expected = String.format(formatStr, 
            containerId, openStatus, dbPath, containerPath, 
            emptyString, hostName, hostName);

