import org.junit.Test;
import static org.junit.Assert.*;

public class JVMIdTest {

    @Test
    public void testCompareToJvmId() {
        // Create two JVMId instances with same jobId and isMap, different jvmId
        JVMId id1 = new JVMId("job1", true, 100L);
        JVMId id2 = new JVMId("job1", true, 200L);
        JVMId id3 = new JVMId("job1", true, 100L);

        // Test comparison between different jvmIds
        assertTrue(id1.compareTo(id2) < 0);
        assertTrue(id2.compareTo(id1) > 0);

        // Test comparison with equal jvmIds
        assertEquals(0, id1.compareTo(id3));

        // Test edge cases with min/max values
        JVMId minId = new JVMId("job1", true, Long.MIN_VALUE);
        JVMId maxId = new JVMId("job1", true, Long.MAX_VALUE);
        assertTrue(minId.compareTo(maxId) < 0);
        assertTrue(maxId.compareTo(minId) > 0);
    }

    // Helper mock JVMId class that replicates the relevant behavior
    static class JVMId implements Comparable<JVMId> {
        String jobId;
        boolean isMap;
        long jvmId;

        public JVMId(String jobId, boolean isMap, long jvmId) {
            this.jobId = jobId;
            this.isMap = isMap;
            this.jvmId = jvmId;
        }

        @Override
        public int compareTo(JVMId that) {
            int jobComp = this.jobId.compareTo(that.jobId);
            if (jobComp == 0) {
                if (this.isMap == that.isMap) {
                    // This is the line that was patched
                    return Long.valueOf(this.jvmId).compareTo(that.jvmId);  // Buggy version
                    // return Long.compare(this.jvmId, that.jvmId);  // Fixed version
                } else {
                    return this.isMap ? -1 : 1;
                }
            }
            return jobComp;
        }
    }
}

