class VersionInfoMojo {
    public String[] getSvnUriInfo(String str) {
        String[] res = new String[2];
        String path = str;
        int index = path.indexOf("branches");

        if (index > -1) {
            res[0] = path.substring(0, index - 1);
            int branchIndex = index + "branches".length() + 1;
            index = path.indexOf('/', branchIndex);  // This is the patched line

            if (index > -1) {
                res[1] = path.substring(branchIndex, index);
            } else {
                res[1] = path.substring(branchIndex);
            }
        }
        return res;
    }
}

